<script>
	let shareButton = document.querySelector(".share-button");
	let sharers = document.querySelector("#sharers");
	let share = document.querySelector("#share");

	window.addEventListener("load", function() {
		sharers.classList.toggle('oculta');
	});

	shareButton.addEventListener("keyup", function(event) {
		event.preventDefault();
		if (a11yClick(event) === true) {

			var window_size = "";
			var url = this.href;
			var domain = url.split("/")[2];
			switch (domain) {
				case "www.facebook.com":
					window_size = "width=585,height=368";
					break;
				case "www.twitter.com":
					window_size = "width=585,height=261";
					break;
				case "plus.google.com":
					window_size = "width=517,height=511";
					break;
				case "www.pinterest.com":
					window_size = "width=700,height=300";
					break;
				case "www.reddit.com":
					window_size = "width=800,height=400";
					break;
				default:
					window_size = "width=585,height=511";
			}
			window.open(url, '', 'menubar=no,toolbar=no,resizable=yes,scrollbars=yes,' + window_size);

		}
	});
	share.addEventListener("keyup", function(event) {
		if(a11yClick(event) === true){
			sharers.classList.toggle('oculta');
	    }
	})
    document.getElementById('share').onclick = function(event) {
		  if(a11yClick(event) === true){
			  sharers.classList.toggle('oculta');
	    }
	  };
</script>